<script>
  import { isActive, url } from "@roxi/routify";
  import { db } from "../../firebase";
  import { generalData } from "../../stores";
  import { joinWorkersAndAttendanceData } from "../../utils";
  import Loader from "./utils/Loader.svelte";
  let isHidden = true;
  let startDate = new Date(
    new Date().getFullYear(),
    new Date().getMonth(),
    new Date().getDate()
  ).getTime();
  let endDate = new Date(
    new Date(
      new Date().getFullYear(),
      new Date().getMonth(),
      new Date().getDate()
    ).setHours(23, 59)
  ).getTime();
  const lazyFetch = async () => {
    let workers = [];
    let attendance = [];
    let queryWorkers = await db.collection("Trabajadores").get();
    let queryAtendence = await db
      .collection("Asistencias")
      .orderBy("fecha")
      .startAt(startDate)
      .endAt(endDate)
      .get();
    queryWorkers.forEach((doc) => {
      workers.push({ ...doc.data(), id: doc.id, ref: doc.ref });
    });
    queryAtendence.forEach((doc) => {
      attendance.push(doc.data());
    });
    let arr = joinWorkersAndAttendanceData(workers, attendance);

    generalData.set(arr);
    return arr;
  };
</script>

